changeSet.sql:
CREATE OR REPLACE FUNCTION check_available_seats() RETURNS TRIGGER AS $$
DECLARE
available_seats INTEGER;
taken_seats INTEGER;
BEGIN
SELECT available_seats INTO available_seats FROM event_schedule WHERE id = NEW.event_schedule_id;
SELECT sum(amount) INTO taken_seats FROM ticket_order WHERE event_schedule_id = NEW.event_schedule_id AND payment_status IN ('BOOKED', 'SALE');
IF NEW.amount + COALESCE(taken_seats, 0) > available_seats THEN
RAISE EXCEPTION 'Not enough seats available';
END IF;

RETURN NEW;
END;
$$ LANGUAGE plpgsql;
